import { useState, useEffect } from 'react';

export default function useInfoAPI(url) {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log('Fetching Data');
      const data = await fetch(url)
        .then(res => res.json());
      setStats(data);
    }
    fetchData();
  }, []);
  return stats;
}
