import useInfoAPI from '../utils/useStats';

export default function CountrySelector() {
  //passing down the full url as a param to 'useInfoAPI'
  const countries = useInfoAPI('https://covid19.mathdro.id/api/countries');
  console.log(countries);  
  //if statement for the loader to kick in (!stats) 
  if (!countries) return <p>Loading...</p>;
  //return someting JSX
  return (
    <div>
      <select>
        {Object.entries(countries.countries).map(([country, code]) => {
          <option key={code} value={code}>{country}</option>;
        })}
      </select>
    </div>
  );
}



